import React, { useState, useEffect } from "react";
import firebase from "./../../config";
import {
  Badge,
  Card,
  CardBody,
  CardHeader,
  Col,
  Pagination,
  PaginationItem,
  PaginationLink,
  Row,
  Table,
  CardImg
} from "reactstrap";

const Random = ({ changeStatusProgress }) => {
  const ref = firebase.database();
  const [users, setUsers] = useState([]);

  useEffect(() => {
    changeStatusProgress(true);
    ref
      .ref("users")
      .orderByValue()
      .on("value", snap => {
        changeStatusProgress(false);
        setUsers(Object.values(snap.val()));
      });
  }, []);
  const renderUser = () => {
    return users.map(user => {
      return (
        <tr key={user.username}>
          <td>
            <CardImg top width="30" src={user.avatar} alt="Card image cap" />
          </td>
          <td>{user.ingame}</td>
          <td>{user.role.map(r => <span key={user.username + r}>{r}, </span>)}</td>
          <td></td>
        </tr>
      );
    });
  };
  return (
    <Row>
      <Col>
        <Card>
          <CardHeader>
            <i className="fa fa-align-justify"></i> Users table
          </CardHeader>
          <CardBody>
            <Table hover bordered striped responsive size="sm">
              <thead>
                <tr>
                  <th>Avatar</th>
                  <th>Ingame</th>
                  <th>Role</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>{renderUser()}</tbody>
            </Table>
          </CardBody>
        </Card>
      </Col>
    </Row>
  );
};

export default Random;
