import React from 'react'
import Widget04 from './../../../views/Widgets/Widget04';
import { Col, Row } from 'reactstrap';
import moment from 'moment'

export default ({ account, historyMatchs }) => {
    console.log(account, historyMatchs)

    const calcAvgMatchOfWeek = () => {
        const weekNumber = moment(historyMatchs[0].dateMatch, "YYYY-MM-DD").week();
        const weekCurrent = moment(moment().format("YYYY-MM-DD"), "YYYY-MM-DD").week();
        const dateWork = (weekCurrent - weekNumber) * 5;
        return parseFloat(historyMatchs.length / 19).toFixed(2)
    }

    return <Row>
        <Col sm="6" md="2">
            <Widget04 icon="icon-people" color="info" header={account.length} value="100">Player</Widget04>
        </Col>
        <Col sm="6" md="2">
            <Widget04 icon="icon-user-follow" color="success" header={historyMatchs.length} value={historyMatchs.length}>Matchs</Widget04>
        </Col>
        <Col sm="6" md="2">
            <Widget04 icon="icon-basket-loaded" color="warning" header={calcAvgMatchOfWeek()} value="25">Avg. Matchs of Week</Widget04>
        </Col>
        <Col sm="6" md="2">
            <Widget04 icon="icon-pie-chart" color="primary" header="28%" value="25">Returning Visitors</Widget04>
        </Col>
        <Col sm="6" md="2">
            <Widget04 icon="icon-speedometer" color="danger" header="5:34:11" value="25">Avg. Matchs of Week</Widget04>
        </Col>
        <Col sm="6" md="2">
            <Widget04 icon="icon-speech" color="info" header="972" value="25"></Widget04>
        </Col>
    </Row>
}
