import React from 'react'
import Widget04 from './../../../views/Widgets/Widget04';
import moment from 'moment'
import { Badge, Card, CardBody, CardHeader, Col, Pagination, PaginationItem, PaginationLink, Row, Table } from 'reactstrap';
import T1 from './T1';
import T2 from './T2';

export default ({ account, historyMatchs }) => {
    const t1 = () => {
        return [...account].sort((a, b) => ((a.win + a.lose) < (b.win + b.lose) ? 1 : (a.win + a.lose) > (b.win + b.lose) ? -1 : 0))
    }
    const t2 = () => {
        return [...account].sort((a, b) => ((a.win) < (b.win) ? 1 : (a.win) > (b.win) ? -1 : 0))
    }

    return <div className="animated fadeIn">
    <Row>
      <Col xs="12" lg="12">
        <Card>
          <CardHeader>
            <i className="fa fa-align-justify"></i> Simple Table
          </CardHeader>
          <CardBody>
            <T1 t1={t1()}/>
            <T2 t2={t2()}/>                 
          </CardBody>
        </Card>
      </Col>
    </Row>
    </div>
}
