import React, { useState } from 'react'
import classnames from 'classnames';
import { Card, CardBody, CardHeader, Col, Row, TabContent, TabPane, Nav, NavItem, NavLink, Button, CardTitle, CardText } from 'reactstrap';
import T1 from './T1';
import T2 from './T2';
import T3 from './T3';
import T4 from './T4';

export default ({ account, historyMatchs }) => {
  const [activeTab, setActiveTab] = useState('1');

  const toggle = tab => {
    if (activeTab !== tab) setActiveTab(tab);
  }
  console.log(historyMatchs)
  const t1 = () => {
    return [...account].sort((a, b) => ((a.win + a.lose) < (b.win + b.lose) ? 1 : (a.win + a.lose) > (b.win + b.lose) ? -1 : 0))
  }
  const t2 = () => {
    return [...account].sort((a, b) => ((a.win) < (b.win) ? 1 : (a.win) > (b.win) ? -1 : 0))
  }

  const t3 = () => {
    return [...account].sort((a, b) => ((a.lose) < (b.lose) ? 1 : (a.lose) > (b.lose) ? -1 : 0))
  }

  const t4 = () => {
    return [...account].sort((a, b) => ((~~((a.win / (a.win + a.lose)) * 100 || 0)) < (~~((b.win / (b.win + b.lose)) * 100 || 0)) ? 1 : (~~((a.win / (a.win + a.lose)) * 100 || 0)) > (~~((b.win / (b.win + b.lose)) * 100 || 0)) ? -1 : 0))
  }

  return <div className="animated fadeIn">
    <Row>
      <Col xs="12" lg="12">
        <Card>
          <CardHeader>
            <i className="fa fa-align-justify"></i> Simple Table
          </CardHeader>
          <CardBody>
            <div>
              <Nav tabs>
                <NavItem>
                  <NavLink
                    className={classnames({ active: activeTab === '1' })}
                    onClick={() => { toggle('1'); }}
                  >
                    Win rates
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={classnames({ active: activeTab === '2' })}
                    onClick={() => { toggle('2'); }}
                  >
                    Number of matches
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={classnames({ active: activeTab === '3' })}
                    onClick={() => { toggle('3'); }}
                  >
                    Number of wins
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    className={classnames({ active: activeTab === '4' })}
                    onClick={() => { toggle('4'); }}
                  >
                    Number of losing
                  </NavLink>
                </NavItem>
              </Nav>
              <TabContent activeTab={activeTab}>
                <TabPane tabId="1">
                  <T4 t4={t4()} />
                </TabPane>
                <TabPane tabId="2">
                  <T1 t1={t1()} />
                </TabPane>
                <TabPane tabId="3">
                  <T2 t2={t2()} />
                </TabPane>
                <TabPane tabId="4">
                  <T3 t3={t3()} />
                </TabPane>
              </TabContent>
            </div>
          </CardBody>
        </Card>
      </Col>
    </Row>
  </div>
}
