import React, { useEffect, useState } from "react";
import {
  Badge,
  Button,
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  Col,
  Collapse,
  Fade,
  Row
} from "reactstrap";
import firebase from "./../../config";
import moment from "moment";

const days = [
  "Monday",
  "Tuesday",
  "Wednesday",
  "Thursday",
  "Friday",
  "Saturday",
  "Sunday"
];

// const styled = {
//     d
// }
const HistoryMatch = () => {
  const [matchs, setMatchs] = useState([]);
  const ref = firebase.database();
  useEffect(() => {
    ref.ref("historyMatch").once("value", snap => {
      if (snap.val() !== null) {
        setMatchs(Object.values(snap.val()));
      }
    });
  }, []);

  const RenderMatchs = ({ match }) => {
    console.log(match);
    return (
      <Col xs="12">
        <Card>
          <CardHeader>
            <strong>
              Match on {days[moment(match.dateMatch).day()]} {match.dateMatch}
            </strong>
          </CardHeader>

          <CardBody>
            <Row>
              <Col xs={12} sm={4} align="center">
                <strong className="d-block">Blue team</strong>
                {
                    match.blueTeam.map((i, index) => <small className="d-block" key={i + index}>{i}</small>)
                }
              </Col>
              <Col xs={12} sm={4}></Col>
              <Col xs={12} sm={4} align="center">
                <strong className="d-block">Red team</strong>
                {
                    match.blueTeam.map((i, index) => <small className="d-block" key={i + index}>{i}</small>)
                }
              </Col>
            </Row>
          </CardBody>
        </Card>
      </Col>
    );
  };

  return (
    <div className="animated fadeIn">
      <Row>
        {!!matchs.length &&
          matchs.map(match => {
            return <RenderMatchs match={match} />;
          })}
      </Row>
    </div>
  );
};

export default HistoryMatch;
