import React from "react";
import { Badge, Card, CardBody, CardHeader, Col, Row, Table } from "reactstrap";

const width = { width: "33%" };

const TableTeams = ({ teams }) => {
  let blueTeam, redTeam;
  teams[0].map(i => {
    const t = i.split(" - ");
    blueTeam = { ...blueTeam, [t[1]]: t[0] };
    return i;
  });
  teams[1].map(i => {
    const t = i.split(" - ");
    redTeam = { ...redTeam, [t[1]]: t[0] };
    return i;
  });

  const handlePos = i => {
    switch (i) {
      case 0:
        return "top";
      case 1:
        return "jungle";
      case 2:
        return "mid";
      case 3:
        return "ad";
      case 4:
        return "support";
      default:
        return;
    }
  };

  return (
    <Row>
      <Col xs="12" style={{ marginTop: 35 }}>
        <Card>
          <CardHeader>
            <i className="fa fa-align-justify"></i> Battle board
          </CardHeader>
          <CardBody>
            <Table responsive size="sm" style={{ textAlign: "center" }}>
              <thead>
                <tr>
                  <th style={width}>Blue Team</th>
                  <th style={width}>vs</th>
                  <th style={width}>Read Team</th>
                </tr>
              </thead>
              <tbody>
                {[0, 1, 2, 3, 4].map((i) => {
                  return (
                    <tr
                      key={
                        blueTeam[handlePos(i)] + redTeam[handlePos(i)]
                      }
                    >
                      <td style={width}>{blueTeam[handlePos(i)]}</td>
                      <td style={width}>{handlePos(i)}</td>
                      <td style={width}>{redTeam[handlePos(i)]}</td>
                    </tr>
                  );
                })}
              </tbody>
            </Table>
          </CardBody>
        </Card>
      </Col>
    </Row>
  );
};

export default TableTeams;
