import React, { useState } from "react";
import {
  Card,
  CardBody,
  CardHeader,
  Col,
  Row,
  ListGroupItem,
  ListGroup,
  FormGroup,
  Label,
  Input
} from "reactstrap";

const OldMatch = ({ oldMatch }) => {
  const [matchs, setMatchs] = useState([]);
    console.log(oldMatch)
  const handleRenderMatch = ({ target: { value } }) => {
    let statisticMatch = [];
    for (let i = 0; i < value; i++) {
      statisticMatch = [
        ...statisticMatch,
        {
          mvpWin: "",
          mvpLose: "",
          blueKill: "",
          redKill: "",
          win: ""
        }
      ];
    }
    setMatchs(statisticMatch);
  };

  const renderTeamPlayer = (nameTeam) => {
    if(nameTeam === 'blueTeam') {
        return oldMatch.blueTeam
    } 
    if(nameTeam === 'redTeam') {
        return oldMatch.redTeam
    }
  }

  const handleTeamWinStatics = (value, match) => {
    let newMatch = matchs;
    newMatch[match] = {...newMatch[match], ...value}
    setMatchs(prev => newMatch)
  }

  return (
    <Row>
      <Col xs="12">
        <Card>
          <CardHeader>
            <i className="fa fa-align-justify"></i> Random match results on{" "}
            {oldMatch.dateMatch}
          </CardHeader>
          <CardBody>
            <Row>
              <Col xs={12} sm={6}>
                <Card>
                  <CardHeader>
                    <strong>Blue Team</strong>
                  </CardHeader>
                  <CardBody>
                    <ListGroup>
                      {oldMatch.blueTeam.map(i => {
                        return <ListGroupItem key={i}> {i} </ListGroupItem>;
                      })}
                    </ListGroup>
                  </CardBody>
                </Card>
              </Col>
              <Col xs={12} sm={6}>
                <Card>
                  <CardHeader>
                    <strong>Red Team</strong>
                  </CardHeader>
                  <CardBody>
                    <ListGroup>
                      {oldMatch.redTeam.map(i => {
                        return <ListGroupItem key={i}> {i} </ListGroupItem>;
                      })}
                    </ListGroup>
                  </CardBody>
                </Card>
              </Col>
              <Col xs={12}>
                <FormGroup>
                  <Label htmlFor="NOM">Number of match:</Label>
                  <Input
                    type="text"
                    id="NOM"
                    placeholder="0"
                    onChange={handleRenderMatch}
                  />
                </FormGroup>
              </Col>
              <Col xs={12}>
                {!!matchs.length &&
                  matchs.map((m, index) => (
                    <Card>
                      <CardHeader>
                        <strong>Match {index + 1}:</strong>
                      </CardHeader>
                      <CardBody>
                        <FormGroup row>
                          <Col md="3">
                            <Label htmlFor="select">Team win:</Label>
                          </Col>
                          <Col xs="12" md="9">
                            <Input type="select" name="select" id="select" onChange={({ target: { value }}) => handleTeamWinStatics({win: value}, index)}>
                              <option value="">Please select</option>
                              <option value="blueTeam">Blue Team</option>
                              <option value="redTeam">Red Team</option>
                            </Input>
                          </Col>
                        </FormGroup>
                        {/* <FormGroup row>
                          <Col md="3">
                            <Label htmlFor="select">MVP team win:</Label>
                          </Col>
                          <Col xs="12" md="9">
                            <Input type="select" name="select" id="select">
                              <option value="">Please select</option>
                              <option value="blueTeam">Blue Team</option>
                              <option value="redTeam">Red Team</option>
                            </Input>
                          </Col>
                        </FormGroup> */}
                      </CardBody>
                    </Card>
                  ))}
              </Col>
            </Row>
          </CardBody>
        </Card>
      </Col>
    </Row>
  );
};

export default OldMatch;
