import React, { useState} from 'react'
import classnames from 'classnames';
import { Card, CardBody, CardHeader, Col, Row, TabContent, TabPane, Nav, NavItem, NavLink, Button, CardTitle, CardText } from 'reactstrap';
import T1 from './T1';
import T2 from './T2';
import T3 from './T3';
import T4 from './T4';

export default ({ account, historyMatchs }) => {
  const [activeTab, setActiveTab] = useState('1');

  const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
  }

  const t1 = () => {
    return [...account].sort((a, b) => ((a.win + a.lose) < (b.win + b.lose) ? 1 : (a.win + a.lose) > (b.win + b.lose) ? -1 : 0))
  }
  const t2 = () => {
    return [...account].sort((a, b) => ((a.win) < (b.win) ? 1 : (a.win) > (b.win) ? -1 : 0))
  }

  const t3 = () => {
    return [...account].sort((a, b) => ((a.lose) < (b.lose) ? 1 : (a.lose) > (b.lose) ? -1 : 0))
  }

  const t4 = () => {
    return [...account].sort((a, b) => ((~~((a.win / (a.win + a.lose)) * 100 || 0)) < (~~((b.win / (b.win + b.lose)) * 100 || 0)) ? 1 : (~~((a.win / (a.win + a.lose)) * 100 || 0)) > (~~((b.win / (b.win + b.lose)) * 100 || 0)) ? -1 : 0))
  }

  return <div className="animated fadeIn">
    <Row>
      <Col xs="12" lg="12">
        <div>
          <Nav tabs>
            <NavItem>
              <NavLink
                className={classnames({ active: activeTab === '1' })}
                onClick={() => { toggle('1'); }}
              >
                Tab1
          </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                className={classnames({ active: activeTab === '2' })}
                onClick={() => { toggle('2'); }}
              >
                Moar Tabs
          </NavLink>
            </NavItem>
          </Nav>
          <TabContent activeTab={activeTab}>
            <TabPane tabId="1">
              <Row>
                <Col sm="12">
                  <h4>Tab 1 Contents</h4>
                </Col>
              </Row>
            </TabPane>
            <TabPane tabId="2">
              <Row>
                <Col sm="6">
                  <Card body>
                    <CardTitle>Special Title Treatment</CardTitle>
                    <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                    <Button>Go somewhere</Button>
                  </Card>
                </Col>
                <Col sm="6">
                  <Card body>
                    <CardTitle>Special Title Treatment</CardTitle>
                    <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                    <Button>Go somewhere</Button>
                  </Card>
                </Col>
              </Row>
            </TabPane>
          </TabContent>
        </div>
        <Card>
          <CardHeader>
            <i className="fa fa-align-justify"></i> Simple Table
          </CardHeader>
          <CardBody>
            <T1 t1={t1()} />
            <T2 t2={t2()} />
            <T3 t3={t3()} />
            <T4 t4={t4()} />
          </CardBody>
        </Card>
      </Col>
    </Row>
  </div>
}
