import React from 'react'
import { Badge, Table, CardImg } from 'reactstrap';

export default ({ t1 }) => {
    const ranking = (r) => {
        switch(r) {
            case 0:return ({background: 'rgba(255, 48, 48, .5)'})
            case 1:return ({background: 'rgba(255, 69, 0, .5)'})
            case 2:return ({background: 'rgba(255, 185, 15, .5)'})
        }
    }
    return <Table responsive>
        <thead>
            <tr>
                <th>#</th>
                <th>Avatar</th>
                <th>Username</th>
                <th>Number of matches</th>
            </tr>
        </thead>
        <tbody>
            {
                t1.map((t, i) => {
                    return <tr style={ranking(i)}>
                        <td><Badge color="success">{i + 1}</Badge></td>
                        <td>
                            <CardImg
                                style={{ width: "30px" }}
                                src={t.avatar}
                                alt="avatar"
                            />
                        </td>
                        <td><Badge color="success">{t.ingame}</Badge></td>
                        <td>
                            <Badge color="success">{t.lose + t.win}</Badge>
                        </td>
                    </tr>
                })
            }

        </tbody>
    </Table>
}
