import React, { useState, useEffect } from "react";
import firebase from "./../../config";
import {
  Badge,
  Card,
  CardBody,
  CardHeader,
  Col,
  Pagination,
  PaginationItem,
  PaginationLink,
  Row,
  Table,
  CardImg
} from "reactstrap";
import { AppSwitch } from "@coreui/react";

const Random = ({ changeStatusProgress }) => {
  const ref = firebase.database();
  const [users, setUsers] = useState([]);

  useEffect(() => {
    changeStatusProgress(true);
    ref
      .ref("users")
      .orderByValue()
      .on("value", snap => {
        changeStatusProgress(false);
        setUsers(Object.values(snap.val()));
      });
  }, []);
  console.log(users)
  const renderUser = () => {
    return users.map((user, index) => {
      return (
        <tr key={user.username}>
          <td>{index + 1}</td>
          <td>
            {user.avatar && (
              <CardImg
                style={{ width: "30px" }}
                src={user.avatar}
                alt="avatar"
              />
            )}
          </td>
          <td>{user.ingame}</td>
          <td>
            {user.role.map(r => (
              <span key={user.username + r}>{r}, </span>
            ))}
          </td>
          <td>
            <small>NA</small>
          </td>
          <td>
            <small>NA</small>
          </td>
          <td>
            <small>NA</small>
          </td>
          <td>
            <AppSwitch className={"mx-1"} color={"success"} />
          </td>
        </tr>
      );
    });
  };
  return (
    <Row>
      <Col>
        <Card>
          <CardHeader>
            <i className="fa fa-align-justify"></i> Users table
          </CardHeader>
          <CardBody>
            <Table hover bordered striped responsive size="sm">
              <thead>
                <tr>
                  <th>#</th>
                  <th>Avatar</th>
                  <th>Ingame</th>
                  <th>Role</th>
                  <th>Win</th>
                  <th>Lost</th>
                  <th>
                    Win rate <small>(%)</small>
                  </th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>{renderUser()}</tbody>
            </Table>
          </CardBody>
        </Card>
      </Col>
    </Row>
  );
};

export default Random;
