import React, { useEffect, useState } from 'react'
import Widget04 from './../../views/Widgets/Widget04';
import { Col, Row } from 'reactstrap';
import firebase from "./../../config";

const Dashboard = () => {
    const ref = firebase.database();
    const [account, setAccount] = useState()
    const [historyMatchs, setHistoryMatchs] = useState()

    const initFunc = async () => {
        const snapshotUser = await ref.ref("users").once("value")
        console.log(snapshotUser.val())
    }

    useEffect(() => {
        initFunc();
        ref
            .ref("historyMatch")
            .orderByValue()
            .on("value", snapshot => {
                if (snapshot.val() !== null) {
                    setHistoryMatchs(Object.values(snapshot.val()));
                }
            });
    }, [])

    console.log(historyMatchs)
    return (<>
        <div className="animated fadeIn">
            <Row>
                <Col sm="6" md="2">
                    <Widget04 icon="icon-people" color="info" header="87.500" value="25">Player</Widget04>
                </Col>
                <Col sm="6" md="2">
                    <Widget04 icon="icon-user-follow" color="success" header="385" value="25">Matchs</Widget04>
                </Col>
                <Col sm="6" md="2">
                    <Widget04 icon="icon-basket-loaded" color="warning" header="1238" value="25">Products sold</Widget04>
                </Col>
                <Col sm="6" md="2">
                    <Widget04 icon="icon-pie-chart" color="primary" header="28%" value="25">Returning Visitors</Widget04>
                </Col>
                <Col sm="6" md="2">
                    <Widget04 icon="icon-speedometer" color="danger" header="5:34:11" value="25">Avg. Matchs of Week</Widget04>
                </Col>
                <Col sm="6" md="2">
                    <Widget04 icon="icon-speech" color="info" header="972" value="25"></Widget04>
                </Col>
            </Row>
        </div>
    </>)
}

export default Dashboard
