import React, { useState, useEffect } from 'react'
import { Card, CardBody, CardHeader, Col, Row, TabContent, TabPane, Nav, NavItem, NavLink, Button, CardTitle, CardText } from 'reactstrap';
import Select from 'react-select';
import firebase from "./../../config";
import { Line } from 'react-chartjs-2';

export default ({ changeStatusProgress }) => {
    const [selectedOption, setSelectedOption] = useState(null)
    const ref = firebase.database();
    const [account, setAccount] = useState([])
    const [historyMatchs, setHistoryMatchs] = useState([])
    
    const options = account.map(i => ({ value: JSON.stringify(i), label: i.ingame }))

    const handleChange = selected => {
        setSelectedOption(selected);
    };
    
    console.log(selectedOption)
    const initFunc = async () => {
        const snapshotUsers = ref.ref("users").once("value")
        const snapshotMatchs = ref.ref("historyMatch").once("value")
        const [users, matchs] = await Promise.all([snapshotUsers, snapshotMatchs])
        setAccount(Object.values(users.val()));
        setHistoryMatchs(Object.values(matchs.val()));
        changeStatusProgress(false)
    }

    useEffect(() => {
        changeStatusProgress(true)
        initFunc();
    }, [])
    const data = {
        labels: [1, 2, 3, 4],
        datasets: [
            {
                label: 'Win match',
                fill: false,
                lineTension: 0.1,
                backgroundColor: '#4dbd74 ',
                borderColor: '#4dbd74 ',
                borderCapStyle: 'butt',
                borderDash: [],
                borderDashOffset: 0.0,
                borderJoinStyle: 'miter',
                pointBorderColor: '#4dbd74 ',
                pointBackgroundColor: '#fff',
                pointBorderWidth: 1,
                pointHoverRadius: 5,
                pointHoverBackgroundColor: '#4dbd74 ',
                pointHoverBorderColor: '#4dbd74 ',
                pointHoverBorderWidth: 2,
                pointRadius: 1,
                pointHitRadius: 10,
                data: [100, 22, 344, 43]
            },
            {
                label: 'Losing match',
                fill: false,
                lineTension: 0.1,
                backgroundColor: '#FF3030',
                borderColor: '#FF3030',
                borderCapStyle: 'butt',
                borderDash: [],
                borderDashOffset: 0.0,
                borderJoinStyle: 'miter',
                pointBorderColor: '#FF3030',
                pointBackgroundColor: '#fff',
                pointBorderWidth: 1,
                pointHoverRadius: 5,
                pointHoverBackgroundColor: '#FF3030',
                pointHoverBorderColor: '#FF3030',
                pointHoverBorderWidth: 2,
                pointRadius: 1,
                pointHitRadius: 10,
                data: [110, 222, 34, 90]
            }
        ]
    };
    return <div className="animated fadeIn">
        <Row>
            <Col xs="12" lg="12">
                <Card>
                    <CardHeader>
                        <i className="fa fa-align-justify"></i> Compare Players
          </CardHeader>
                    <CardBody>
                        <Select
                            value={selectedOption}
                            onChange={handleChange}
                            options={options}
                            isMulti
                        />
                        <Line data={data} />
                    </CardBody>
                </Card>
            </Col>
        </Row>
    </div>
}
