import React, {useState} from 'react'
import { Card, CardBody, CardHeader, Col, Row, FormGroup, Label, Input, Progress } from 'reactstrap';

export default ({ account, statisticWLPlayers }) => {
  const [name, setName] = useState('');
  
  const statisticChooseName = statisticWLPlayers[name]
  const totalMatchs = () => {
    if(!account.length || !name) return;
    const player = [...account].filter(e => e.ingame === name)[0]
    return player.win + player.lose
  }
  const renderStatistic = () => {
    if(!name) return;
    const w = Object.values(statisticChooseName.win)
    const l = Object.values(statisticChooseName.lose)
    console.log(w,l)
  }
  renderStatistic();
  return <div className="animated fadeIn">
    <Row>
      <Col xs="12" lg="12">
        <Card>
          <CardHeader>
            <i className="fa fa-align-justify"></i> Statistic Players
          </CardHeader>
          <CardBody>
            <FormGroup row>
              <Col xs="12" md ="12">
                <Label htmlFor="select">choose the player</Label>
              </Col>
              <Col xs="12" md="4">
                <Input type="select" name="select" id="select" onChange={ e => setName(e.target.value) }>
                  <option value="0">Please choose</option>
                  {
                    account.map(acc => {
                      return <option value={acc.ingame} key={acc.ingame}>{acc.ingame}</option>
                    })
                  }
                </Input>
              </Col>
            </FormGroup>

            <Row>
              <Col xs="12" md="3">
                  'PS.HÃ¡ckMap'
              </Col>
              <Col xs="12" md="9">
                <Progress animated color="success" value="25" style={{ height: 7, marginBottom: 5 }}/>
                <Progress animated color="danger" value="100" style={{ height: 7 }}/>
              </Col>
            </Row>
          </CardBody>
        </Card>
      </Col>
    </Row>
  </div>
}
