import React, { Component, useState } from "react";
import {
  Button,
  Card,
  CardBody,
  CardFooter,
  Col,
  Container,
  Form,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Row
} from "reactstrap";

const styleErr = { border: "1px solid #FF3300" };

function validateEmail(email) {
  var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  return re.test(String(email).toLowerCase());
}
function sleep(time) {
  return new Promise(resolve => setTimeout(resolve, time));
}
const Register = () => {
  const [account, setAccout] = useState({
    username: "",
    email: "",
    password: "",
    repPassword: ""
  });
  const [err, setErr] = useState({});
  const handleRegister = async () => {
    let e = false;
    if (account.repPassword !== account.password || !account.password) {
      setErr(prev => ({ ...prev, password: true }));
      e = true;
    }
    if (!account.username && account.username.trim()) {
      setErr(prev => ({ ...prev, username: true }));
      e = true;
    }
    if (!validateEmail(account.email)) {
      setErr(prev => ({ ...prev, email: true }));
      e = true;
    }
    if(e) return;
    await setAccout(prev =>  delete prev.repPassword);
    console.log(account)
  };

  return (
    <div className="app flex-row align-items-center">
      <Container>
        <Row className="justify-content-center">
          <Col md="9" lg="7" xl="6">
            <Card className="mx-4">
              <CardBody className="p-4">
                <Form>
                  <h1>Register</h1>
                  <p className="text-muted">Create your account</p>
                  <InputGroup
                    className="mb-3"
                    style={err.username ? styleErr : {}}
                  >
                    <InputGroupAddon addonType="prepend">
                      <InputGroupText>
                        <i className="icon-user"></i>
                      </InputGroupText>
                    </InputGroupAddon>
                    <Input
                      type="text"
                      placeholder="Username"
                      autoComplete="username"
                      value={account.username}
                      onChange={({ target: { value } }) => {
                        setAccout(prev => ({ ...prev, username: value }));
                        setErr(prev => ({ ...prev, username: false }));
                      }}
                    />
                  </InputGroup>
                  <InputGroup
                    className="mb-3"
                    style={err.email ? styleErr : {}}
                  >
                    <InputGroupAddon addonType="prepend">
                      <InputGroupText>@</InputGroupText>
                    </InputGroupAddon>
                    <Input
                      type="text"
                      placeholder="Email"
                      autoComplete="email"
                      value={account.email}
                      onChange={({ target: { value } }) => {
                        setAccout(prev => ({ ...prev, email: value }));
                        setErr(prev => ({ ...prev, email: false }));
                      }}
                    />
                  </InputGroup>
                  <InputGroup
                    className="mb-3"
                    style={err.password ? styleErr : {}}
                  >
                    <InputGroupAddon addonType="prepend">
                      <InputGroupText>
                        <i className="icon-lock"></i>
                      </InputGroupText>
                    </InputGroupAddon>
                    <Input
                      type="password"
                      placeholder="Password"
                      autoComplete="new-password"
                      value={account.password}
                      onChange={({ target: { value } }) => {
                        setAccout(prev => ({ ...prev, password: value }));
                        setErr(prev => ({ ...prev, password: false }));
                      }}
                    />
                  </InputGroup>
                  <InputGroup
                    className="mb-4"
                    style={err.password ? styleErr : {}}
                  >
                    <InputGroupAddon addonType="prepend">
                      <InputGroupText>
                        <i className="icon-lock"></i>
                      </InputGroupText>
                    </InputGroupAddon>
                    <Input
                      type="password"
                      placeholder="Repeat password"
                      autoComplete="new-password"
                      value={account.repPassword}
                      onChange={({ target: { value } }) => {
                        setAccout(prev => ({ ...prev, repPassword: value }));
                        setErr(prev => ({ ...prev, password: false }));
                      }}
                    />
                  </InputGroup>
                  <Button color="success" block onClick={handleRegister}>
                    Create Account
                  </Button>
                </Form>
              </CardBody>
              <CardFooter className="p-4">
                <Row>
                  <Col xs="12" sm="6">
                    <Button className="btn-facebook mb-1" block>
                      <span>facebook</span>
                    </Button>
                  </Col>
                  <Col xs="12" sm="6">
                    <Button className="btn-twitter mb-1" block>
                      <span>twitter</span>
                    </Button>
                  </Col>
                </Row>
              </CardFooter>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Register;
